const storedContact=JSON.parse(localStorage.getItem("Contact")),contactEl=document.getElementsByClassName("contact")[0];for(var key in storedContact){const t=storedContact[key];for(var i=0;i<t.length;i++){var detail=t[i];contactEl.innerHTML+=`\n        <section class="${key}">\n        <div class="container" id="text-block">\n            <h1>${detail.name}</h1>\n                <p>${detail.username}</p>\n                <a href="${detail.url}"><img src="${detail.icon}" alt="" srcset="" height="32" width="32"></a>\n                    \n         </div>\n                    `}}