const storedProjects=JSON.parse(localStorage.getItem("projects")),projectsEl=document.getElementsByClassName("projects")[0];for(var key in storedProjects){const e=storedProjects[key];for(var i=0;i<e.length;i++){var detail=e[i];projectsEl.innerHTML+=`\n    <section id="text-block" class="${key} container">\n    <h2>${detail.title}</h2>\n        <div class="col-10 align-self-center container">\n        \n            <p><a href="${detail.link}"><img src="../images/github.png" alt="" width="64px" height="64px">\n                Link to the Project</a></p>\n            <p>Date completed: ${detail.date}</p>\n            <p>Technology used: ${detail.tech}</p>\n            <p>Teammates involved: ${detail.team}</p>\n            <p>${detail.content}</p>\n                \n        </div>\n     </section>\n                `}}